package com.cls.avengers.exceptions.httpexceptions;import lombok.NoArgsConstructor;import org.springframework.http.HttpStatus;import org.springframework.web.bind.annotation.ResponseStatus;@NoArgsConstructor@ResponseStatus(HttpStatus.BAD_REQUEST)public class HttpBadRequestException extends HttpResponseException {        public HttpBadRequestException(String message, String errorCode, Throwable cause) {        super(message, errorCode, cause);    }        public HttpBadRequestException(String message) {        super(message);    }        public HttpBadRequestException(Throwable cause) {        super(cause);    }}