package com.cls.avengers.exceptions.httpexceptions;import lombok.NoArgsConstructor;import org.springframework.http.HttpStatus;import org.springframework.web.bind.annotation.ResponseStatus;@NoArgsConstructor@ResponseStatus(HttpStatus.CONFLICT)public class HttpConflictException extends HttpResponseException {        public HttpConflictException(String message, String errorCode, Throwable cause) {        super(message, errorCode, cause);    }        public HttpConflictException(String message) {        super(message);    }        public HttpConflictException(Throwable cause) {        super(cause);    }}