package com.cls.avengers.exceptions.httpexceptions;import lombok.NoArgsConstructor;import org.springframework.http.HttpStatus;import org.springframework.web.bind.annotation.ResponseStatus;@NoArgsConstructor@ResponseStatus(HttpStatus.NOT_FOUND)public class HttpNotFoundException extends HttpResponseException {        public HttpNotFoundException(String message, String errorCode, Throwable cause) {        super(message, errorCode, cause);    }        public HttpNotFoundException(String message) {        super(message);    }        public HttpNotFoundException(Throwable cause) {        super(cause);    }}