package com.cls.avengers.exceptions.httpexceptions;import lombok.Getter;/** * Abstract class for HttpResponseException */public abstract class HttpResponseException extends RuntimeException {    @Getter    protected final String errorCode;    public HttpResponseException() {        super();        this.errorCode = null;    }    public HttpResponseException(String message, String errorCode, Throwable cause) {        super(message, cause);        this.errorCode = errorCode;    }    public HttpResponseException(String message) {        super(message);        this.errorCode = null;    }    public HttpResponseException(Throwable cause) {        super(cause);        this.errorCode = null;    }}